import React, { useState, useEffect } from 'react';
import '../css/Assets.css';
import TopNav from '../pages/TopNav';

function Assets() {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [showFullTable, setShowFullTable] = useState(false);
  const [searchInput, setSearchInput] = useState('');
  const [editFormData, setEditFormData] = useState(null);

  const [departmentOptions, setDepartmentOptions] = useState(['HR', 'Finance', 'Engineering']);
  const [makeOptions, setMakeOptions] = useState(['Dell', 'HP', 'Lenovo']);
  const [itemsOptions, setItemsOptions] = useState(['Laptop', 'Desktop', 'Tablet']);
  const [processorOptions, setProcessorOptions] = useState(['Intel', 'AMD']);
  const [speedOptions, setSpeedOptions] = useState(['2.5 GHz', '3.0 GHz', '3.5 GHz']);
  const [ramOptions, setRamOptions] = useState(['8 GB', '16 GB', '32 GB']);
  const [hddOptions, setHddOptions] = useState(['500 GB', '1 TB', '2 TB']);
  const [ssdOptions, setSsdOptions] = useState(['128 GB', '256 GB', '512 GB']);
  const [monitorOptions, setMonitorOptions] = useState(['21 inch', '24 inch', '27 inch']);
  const [osOptions, setOsOptions] = useState(['Windows', 'MacOS', 'Linux']);

  const [newOptionField, setNewOptionField] = useState('');
  const [newOptionValue, setNewOptionValue] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    showCurrentPage();
  }, [currentPage, itemsPerPage, data]);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/admin/genReport');
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const changeItemsPerPage = (event) => {
    setItemsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1);
  };

  const nextPage = () => {
    if (currentPage * itemsPerPage < data.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const showCurrentPage = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const tableRows = document.getElementById("employeeTable").querySelectorAll("tbody tr");

    tableRows.forEach((row, index) => {
      if (index >= startIndex && index < endIndex) {
        row.style.display = "";
      } else {
        row.style.display = "none";
      }
    });

    document.getElementById("prevPageBtn").disabled = (currentPage === 1);
    document.getElementById("nextPageBtn").disabled = (endIndex >= data.length);
  };

  const openForm = () => {
    document.getElementById("addDataForm").style.display = "block";
  };

  const closeForm = () => {
    document.getElementById("addDataForm").style.display = "none";
  };

  const openFieldForm = () => {
    document.getElementById("addFieldForm").style.display = "block";
  };

  const closeFieldForm = () => {
    document.getElementById("addFieldForm").style.display = "none";
  };

  const toggleColumns = () => {
    setShowFullTable(!showFullTable);
    const extraColumns = document.querySelectorAll(".extra-column");

    extraColumns.forEach(column => {
      column.style.display = showFullTable ? "table-cell" : "none";
    });
  };

  const searchTable = () => {
    const filter = searchInput.toUpperCase();
    const table = document.getElementById("employeeTable");
    const tr = table.getElementsByTagName("tr");

    for (let i = 1; i < tr.length; i++) {
      let display = "none";
      const td = tr[i].getElementsByTagName("td");
      for (let j = 0; j < td.length; j++) {
        const txtValue = td[j].textContent || td[j].innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
          display = "";
          break;
        }
      }
      tr[i].style.display = display;
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/delete_asset/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        const updatedData = data.filter(item => item._id !== id);
        setData(updatedData);
      } else {
        console.error('Failed to delete item');
      }
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const addField = async (e) => {
    e.preventDefault();
  
    const fieldName = document.getElementById('fieldName').value;
    const fieldType = document.getElementById('fieldType').value;
  
    try {
      const response = await fetch('http://localhost:5000/addField', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fieldName, fieldType }),
      });
  
      if (response.ok) {
        closeFieldForm();
        fetchData();
      } else {
        console.error('Failed to add field');
      }
    } catch (error) {
      console.error('Error adding field:', error);
    }
  };

  const openEditForm = (item) => {
    setEditFormData(item);
    document.getElementById("editDataForm").style.display = "block";
  };

  const closeEditForm = () => {
    setEditFormData(null);
    document.getElementById("editDataForm").style.display = "none";
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const updatedItem = {};
    formData.forEach((value, key) => {
      if (value) updatedItem[key] = value;
    });

    try {
      const response = await fetch(`http://localhost:5000/edit_asset/${editFormData._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedItem),
      });

      if (response.ok) {
        closeEditForm();
        fetchData();
      } else {
        console.error('Failed to update item');
      }
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  const openAddOptionModal = (field) => {
    setNewOptionField(field);
    document.getElementById("addOptionForm").style.display = "block";
  };

  const closeAddOptionForm = () => {
    setNewOptionField('');
    setNewOptionValue('');
    document.getElementById("addOptionForm").style.display = "none";
  };

  const handleAddOption = () => {
    if (!newOptionValue) return;

    switch (newOptionField) {
      case 'Department':
        setDepartmentOptions([...departmentOptions, newOptionValue]);
        break;
      case 'Make':
        setMakeOptions([...makeOptions, newOptionValue]);
        break;
      case 'ITEMS':
        setItemsOptions([...itemsOptions, newOptionValue]);
        break;
      case 'Processor':
        setProcessorOptions([...processorOptions, newOptionValue]);
        break;
      case 'Speed':
        setSpeedOptions([...speedOptions, newOptionValue]);
        break;
      case 'RAM':
        setRamOptions([...ramOptions, newOptionValue]);
        break;
      case 'HDD':
        setHddOptions([...hddOptions, newOptionValue]);
        break;
      case 'SSD':
        setSsdOptions([...ssdOptions, newOptionValue]);
        break;
      case 'Monitor':
        setMonitorOptions([...monitorOptions, newOptionValue]);
        break;
      case 'Operating System':
        setOsOptions([...osOptions, newOptionValue]);
        break;
      default:
        break;
    }

    closeAddOptionForm();
  };

  return (
    <div className="report-page">
      <TopNav />
      <div className="content gen-report-content">
        <div className="table-container">
          <div className="items-per-page">
            <label htmlFor="itemsPerPage">Items per page:</label>
            <select id="itemsPerPage" onChange={changeItemsPerPage} value={itemsPerPage}>
              <option value="5">5</option>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value="50">50</option>
              <option value="100">100</option>
            </select>
          </div>
          <button onClick={toggleColumns} id="toggleColumnsBtn">Toggle Columns</button>
          <button onClick={openForm} id="openFormBtn">Add Data</button>
          <button onClick={openFieldForm} id="openFieldFormBtn">Add Field</button>
          <input
            type="text"
            id="searchInput"
            placeholder="Search..."
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            onKeyUp={searchTable}
          />
          <table id="employeeTable" className="data-table">
            <thead>
              <tr>
                {data.length > 0 && Object.keys(data[0]).map((key) => (
                  <th key={key}>{key}</th>
                ))}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item) => (
                <tr key={item._id}>
                  {Object.keys(item).map((key) => (
                    <td key={key} className={showFullTable ? '' : 'extra-column'}>
                      {item[key]}
                    </td>
                  ))}
                  <td>
                    <button onClick={() => handleDelete(item._id)}>Delete</button>
                    <button onClick={() => openEditForm(item)}>Edit</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="pagination">
            <button onClick={prevPage} id="prevPageBtn">Previous</button>
            <button onClick={nextPage} id="nextPageBtn">Next</button>
          </div>
        </div>

        {/* Add Data Form */}
        <div id="addDataForm" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeForm}>&times;</span>
            <h2>Add New Data</h2>
            <form id="addForm">
              {data.length > 0 && Object.keys(data[0]).map((key) => (
                key !== '_id' && (
                  <div key={key}>
                    <label htmlFor={key}>{key}:</label>
                    <input type="text" id={key} name={key} />
                  </div>
                )
              ))}
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>

        {/* Add Field Form */}
        <div id="addFieldForm" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeFieldForm}>&times;</span>
            <h2>Add New Field</h2>
            <form id="fieldForm" onSubmit={addField}>
              <label htmlFor="fieldName">Field Name:</label>
              <input type="text" id="fieldName" name="fieldName" required />
              <label htmlFor="fieldType">Field Type:</label>
              <input type="text" id="fieldType" name="fieldType" required />
              <button type="submit">Add Field</button>
            </form>
          </div>
        </div>

        {/* Edit Data Form */}
        <div id="editDataForm" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeEditForm}>&times;</span>
            <h2>Edit Data</h2>
            {editFormData && (
              <form id="editForm" onSubmit={handleEditSubmit}>
                {Object.keys(editFormData).map((key) => (
                  key !== '_id' && (
                    <div key={key}>
                      <label htmlFor={key}>{key}:</label>
                      {(key === 'Department' || key === 'Make' || key === 'ITEMS' || key === 'Processor' ||
                        key === 'Speed' || key === 'RAM' || key === 'HDD' || key === 'SSD' ||
                        key === 'Monitor' || key === 'Operating System') ? (
                        <select id={key} name={key} defaultValue={editFormData[key]}>
                          {(key === 'Department' ? departmentOptions :
                            key === 'Make' ? makeOptions :
                            key === 'ITEMS' ? itemsOptions :
                            key === 'Processor' ? processorOptions :
                            key === 'Speed' ? speedOptions :
                            key === 'RAM' ? ramOptions :
                            key === 'HDD' ? hddOptions :
                            key === 'SSD' ? ssdOptions :
                            key === 'Monitor' ? monitorOptions : osOptions
                          ).map(option => (
                            <option key={option} value={option}>{option}</option>
                          ))}
                        </select>
                      ) : (
                        <input
                          type="text"
                          id={key}
                          name={key}
                          defaultValue={editFormData[key]}
                        />
                      )}
                      <button type="button" onClick={() => openAddOptionModal(key)}>Add New Option</button>
                    </div>
                  )
                ))}
                <button type="submit">Submit</button>
              </form>
            )}
          </div>
        </div>

        {/* Add Option Form */}
        <div id="addOptionForm" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeAddOptionForm}>&times;</span>
            <h2>Add New Option to {newOptionField}</h2>
            <input
              type="text"
              value={newOptionValue}
              onChange={(e) => setNewOptionValue(e.target.value)}
              placeholder={`Enter new option for ${newOptionField}`}
            />
            <button onClick={handleAddOption}>Add</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Assets;
