import React, { useState, useEffect } from 'react';

const EditAssetForm = ({ item }) => {
    const [formData, setFormData] = useState({
        LocationName: item.LocationName || '',
        Plant: item.Plant || '',
        Department: item.Department || '',
        // Initialize other fields here based on your item structure
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSelectChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleAddNew = (field) => {
        const newValue = prompt(`Please enter new ${field.replace('_', ' ')}:`);
        if (newValue) {
            // Update formData for dynamic fields
            setFormData({
                ...formData,
                [field]: newValue
            });
            // Handle storing new option in localStorage if needed
            // Example: storeNewOption(field + 's', newValue);
        }
    };

    // Example: useEffect for loading stored options from localStorage
    // useEffect(() => {
    //     loadStoredOptions('Department', 'departments');
    // }, []);

    return (
        <div className="edit-form-container">
            {item ? (
                <form action={`/update_asset/${item._id}`} method="POST">
                    <label htmlFor="LocationName">Location Name:</label>
                    <select id="LocationName" name="LocationName" value={formData.LocationName} onChange={handleSelectChange}>
                        <option value="RANCHI">RANCHI</option>
                        <option value="Other">Other</option>
                    </select>

                    <label htmlFor="Plant">Plant:</label>
                    <select id="Plant" name="Plant" value={formData.Plant} onChange={handleSelectChange}>
                        <option value="WWR">WWR</option>
                        <option value="SPCD">SPCD</option>
                        <option value="CPP">CPP</option>
                    </select>

                    <label htmlFor="Department">Department:</label>
                    <div className="input-container">
                        <select id="Department" name="Department" value={formData.Department} onChange={handleSelectChange}>
                            <option value="HRD">HRD</option>
                            <option value="SPCD WEIGHBRIDGE">SPCD WEIGHBRIDGE</option>
                            <option value="LINE 5">LINE 5</option>
                            <option value="SALES">SALES</option>
                            <option value="LEE FOUR">LEE FOUR</option>
                            <option value="MATERIAL CONTROL (PLANT OFFICE)">MATERIAL CONTROL (PLANT OFFICE)</option>
                            <option value="WB">WB</option>
                        </select>
                        <button type="button" className="add-new" onClick={() => handleAddNew('Department')}>Add New</button>
                    </div>

                    {/* Repeat for other dynamic fields */}
                    <button type="submit">Update</button>
                </form>
            ) : (
                <p className="error-message">Asset not found.</p>
            )}
        </div>
    );
};

export default EditAssetForm;
