import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/Assets.css';
import TopNav from '../pages/TopNav'; // Assuming TopNav.jsx is correctly imported

function Assets() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData(); // Fetch data on component mount
  }, []); // Empty dependency array ensures this effect runs only once

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/admin/genReport');
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`/delete_asset/${id}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        // Remove the item from state
        const updatedData = data.filter(item => item._id !== id);
        setData(updatedData);
      } else {
        console.error('Failed to delete item');
      }
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <div className="report-page">
      <TopNav /> {/* Include TopNav component */}
      <div className="content gen-report-content">
        <div className="table-container">
          <table id="employeeTable">
            <thead>
              <tr>
                {data.length > 0 &&
                  Object.keys(data[0]).map((key, index) => (
                    key !== '_id' && (
                      <th key={key} className={index > 8 ? 'extra-column' : ''}>{key}</th>
                    )
                  ))}
                <th className="extra-column">Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item) => (
                <tr key={item._id}>
                  {Object.keys(item).map((key, colIndex) => (
                    key !== '_id' && (
                      <td key={`${item._id}-${key}`} className={colIndex > 8 ? 'extra-column' : ''}>{item[key]}</td>
                    )
                  ))}
                  <td className="extra-column">
                    <Link to={`/edit_asset/${item._id}`} className="edit-link">Edit</Link>
                    <button className="delete-link" onClick={() => handleDelete(item._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Assets;
