import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function EditAsset() {
  const { id } = useParams();
  const [asset, setAsset] = useState({});
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetchAsset();
  }, []);

  const fetchAsset = async () => {
    try {
      const response = await fetch(`http://localhost:5000/admin/getAsset/${id}`);
      const assetData = await response.json();
      setAsset(assetData);
      setFormData(assetData); // Initialize form data with current asset data
    } catch (error) {
      console.error('Error fetching asset:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/admin/updateAsset/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Handle success (maybe show a success message)
        // Example: history.push('/assets');
      } else {
        console.error('Failed to update asset');
      }
    } catch (error) {
      console.error('Error updating asset:', error);
    }
  };

  return (
    <div className="edit-asset-page">
      <h2>Edit Asset</h2>
      <form onSubmit={handleSubmit}>
        {Object.keys(asset).map((key) => (
          <div key={key}>
            <label htmlFor={key}>{key}:</label>
            <input
              type="text"
              id={key}
              name={key}
              value={formData[key] || ''}
              onChange={handleChange}
              required
            /><br />
          </div>
        ))}
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
}

export default EditAsset;
