import React, { useState, useEffect } from 'react';
import '../css/Emplist.css';
import TopNav from './TopNav'; // Import the TopNav component

const Emplist = () => {
    const [data, setData] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [filter, setFilter] = useState('');
    const [selectedAsset, setSelectedAsset] = useState(null);

    useEffect(() => {
        fetchData();
    }, []);

    useEffect(() => {
        showCurrentPage(); // Update table display on pagination or item per page change
    }, [currentPage, itemsPerPage, data]); // Run whenever currentPage, itemsPerPage, or data changes

    const fetchData = () => {
        // Fetch data from server
        fetch('http://localhost:5000/emplist')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok ' + response.statusText);
                }
                return response.json();
            })
            .then(data => setData(data))
            .catch(error => console.error('Error fetching data:', error));
    };

    const filteredData = data.filter(item => {
        return Object.values(item).some(value =>
            value.toString().toLowerCase().includes(filter.toLowerCase())
        );
    });

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = filteredData.slice(indexOfFirstItem, indexOfLastItem);

    const changePage = (pageNumber) => setCurrentPage(pageNumber);
    const changeItemsPerPage = (event) => setItemsPerPage(parseInt(event.target.value));

    const openModal = (id) => {
        fetch(`http://localhost:5000/get_asset/${id}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok ' + response.statusText);
                }
                return response.json();
            })
            .then(data => setSelectedAsset(data))
            .catch(error => console.error('Error fetching asset details:', error));
    };

    const closeModal = () => setSelectedAsset(null);

    const openEditForm = (item) => {
        // Redirect to the edit form page or open the edit form modal
        window.location.href = `/edit_asset/${item._id}`;
    };

    const handleDelete = (id) => {
        if (window.confirm('Are you sure you want to delete this item?')) {
            // Handle deletion
            fetch(`http://localhost:5000/delete_asset/${id}`, {
                method: 'DELETE'
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok ' + response.statusText);
                }
                // Remove the deleted item from the state
                setData(data.filter(item => item._id !== id));
            })
            .catch(error => console.error('Error deleting asset:', error));
        }
    };

    const showCurrentPage = () => {
        const startIndex = (currentPage - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        const tableRows = document.getElementById("employeeTable").querySelectorAll("tbody tr");

        tableRows.forEach((row, index) => {
            if (index >= startIndex && index < endIndex) {
                row.style.display = "";
            } else {
                row.style.display = "none";
            }
        });

        // Update pagination buttons
        document.getElementById("prevPageBtn").disabled = (currentPage === 1);
        document.getElementById("nextPageBtn").disabled = (endIndex >= filteredData.length);
    };

    const searchTable = (e) => {
        const filterValue = e.target.value.toUpperCase();
        const table = document.getElementById("employeeTable");
        const tr = table.getElementsByTagName("tr");

        for (let i = 1; i < tr.length; i++) { // Start from 1 to skip table headers
            let display = "none";
            const td = tr[i].getElementsByTagName("td");
            for (let j = 0; j < td.length; j++) {
                const txtValue = td[j].textContent || td[j].innerText;
                if (txtValue.toUpperCase().indexOf(filterValue) > -1) {
                    display = "";
                    break;
                }
            }
            tr[i].style.display = display;
        }
    };

    return (
        <div className="app">
            <TopNav /> {/* Add the TopNav component here */}
            <div className="content gen-report-content">
                <div className="table-container">
                    <div className="items-per-page">
                        <label htmlFor="itemsPerPage">Items per page:</label>
                        <select id="itemsPerPage" value={itemsPerPage} onChange={changeItemsPerPage}>
                            <option value="10">10</option>
                            <option value="20">20</option>
                            <option value="50">50</option>
                            <option value="100">100</option>
                        </select>
                    </div>
                    <div className="filter-container">
                        <label htmlFor="filterInput">Filter:</label>
                        <input
                            type="text"
                            id="filterInput"
                            value={filter}
                            onChange={(e) => setFilter(e.target.value)}
                            onKeyUp={searchTable}
                            placeholder="Search"
                        />
                    </div>
                    <table id="employeeTable">
                        <thead>
                            <tr>
                                {data.length > 0 &&
                                    Object.keys(data[0]).map(key => (
                                        key !== '_id' && <th key={key}>{key}</th>
                                    ))
                                }
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {currentItems.map(item => (
                                <tr key={item._id}>
                                    {Object.keys(item).map(key => (
                                        key !== '_id' && <td key={key}>{item[key]}</td>
                                    ))}
                                    <td>
                                        <button className="edit-link" onClick={() => openEditForm(item)}>Edit</button>
                                        <button
                                            className="delete-link"
                                            onClick={() => handleDelete(item._id)}
                                        >
                                            Delete
                                        </button>
                                        <button
                                            className="view-link"
                                            onClick={() => openModal(item._id)}
                                        >
                                            View
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="pagination">
                        <button id="prevPageBtn" onClick={() => changePage(currentPage - 1)}>Previous Page</button>
                        <button id="nextPageBtn" onClick={() => changePage(currentPage + 1)}>Next Page</button>
                    </div>
                </div>
            </div>

            {selectedAsset && (
                <div id="viewModal" className="modal" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <span className="close-button" onClick={closeModal}>&times;</span>
                        <h2>Asset Details</h2>
                        <div id="assetDetails" className="asset-details">
                            {Object.keys(selectedAsset).map(key => (
                                key !== '_id' && <p key={key}><strong>{key}:</strong> {selectedAsset[key]}</p>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Emplist;
