import React, { useState, useEffect } from 'react';
import '../css/Assets.css';
import TopNav from '../pages/TopNav'; // Assuming TopNav.jsx is correctly imported

function Assets() {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [searchInput, setSearchInput] = useState('');
  const [editFormData, setEditFormData] = useState(null); // State to hold the data of the item being edited

  useEffect(() => {
    fetchData(); // Fetch data on component mount
  }, []); // Empty dependency array ensures this effect runs only once

  useEffect(() => {
    showCurrentPage(); // Update table display on pagination or item per page change
  }, [currentPage, itemsPerPage, data]); // Run whenever currentPage, itemsPerPage, or data changes

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/admin/genReport');
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const changeItemsPerPage = (event) => {
    setItemsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1); // Reset to first page when items per page changes
  };

  const nextPage = () => {
    if ((currentPage + 1) * itemsPerPage <= data.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const showCurrentPage = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    const tableRows = document.getElementById("employeeTable").querySelectorAll("tbody tr");

    tableRows.forEach((row, index) => {
      if (index >= startIndex && index < endIndex) {
        row.style.display = "";
      } else {
        row.style.display = "none";
      }
    });

    // Update pagination buttons
    document.getElementById("prevPageBtn").disabled = (currentPage === 1);
    document.getElementById("nextPageBtn").disabled = ((currentPage + 1) * itemsPerPage > tableRows.length);
  };

  const openForm = () => {
    document.getElementById("addDataForm").style.display = "block";
  };

  const closeForm = () => {
    document.getElementById("addDataForm").style.display = "none";
  };

  const openEditForm = (item) => {
    setEditFormData(item);
    document.getElementById("editDataForm").style.display = "block";
  };

  const closeEditForm = () => {
    setEditFormData(null);
    document.getElementById("editDataForm").style.display = "none";
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const updatedItem = {};
    formData.forEach((value, key) => {
      if (value) updatedItem[key] = value; // Only update fields with a non-empty value
    });

    try {
      const response = await fetch(`http://localhost:5000/edit_asset/${editFormData._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedItem),
      });

      if (response.ok) {
        closeEditForm();
        fetchData(); // Refresh data to show the updated item
      } else {
        console.error('Failed to update item');
      }
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/delete_asset/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        const updatedData = data.filter(item => item._id !== id);
        setData(updatedData);
      } else {
        console.error('Failed to delete item');
      }
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const searchTable = () => {
    const filter = searchInput.toUpperCase();
    const table = document.getElementById("employeeTable");
    const tr = table.getElementsByTagName("tr");

    for (let i = 1; i < tr.length; i++) { // Start from 1 to skip table headers
      let display = "none";
      const td = tr[i].getElementsByTagName("td");
      for (let j = 0; j < td.length; j++) {
        const txtValue = td[j].textContent || td[j].innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
          display = "";
          break;
        }
      }
      tr[i].style.display = display;
    }
  };

  return (
    <div className="report-page">
      <TopNav /> {/* Include TopNav component */}
      <div className="content gen-report-content">
        <div className="table-container">
          <div className="items-per-page">
            <label htmlFor="itemsPerPage">Items per page:</label>
            <select id="itemsPerPage" onChange={changeItemsPerPage} value={itemsPerPage}>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value="50">50</option>
              <option value="100">100</option>
            </select>
          </div>

          <input
            type="text"
            id="searchInput"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            onKeyUp={searchTable}
            placeholder="Search for names.."
          />

          <button id="addDataBtn" onClick={openForm}>Add Data</button>

          <div id="addDataForm" className="modal">
            <div className="modal-content">
              <span className="close" onClick={closeForm}>&times;</span>
              <h2>Add New Data</h2>
              <form id="dataForm">
                {data.length > 0 &&
                  Object.keys(data[0]).map((key) => (
                    key !== '_id' && (
                      <div key={key}>
                        <label htmlFor={key}>{key}:</label>
                        <input type="text" id={key} name={key} /><br />
                      </div>
                    )
                  ))}
                <button type="submit">Submit</button>
              </form>
            </div>
          </div>

          <div id="editDataForm" className="modal">
            <div className="modal-content">
              <span className="close" onClick={closeEditForm}>&times;</span>
              <h2>Edit Data</h2>
              <form id="editForm" onSubmit={handleEditSubmit}>
                {editFormData &&
                  Object.keys(editFormData).map((key) => (
                    key !== '_id' && (
                      <div key={key}>
                        <label htmlFor={key}>{key}:</label>
                        <input type="text" id={key} name={key} defaultValue={editFormData[key]} /><br />
                      </div>
                    )
                  ))}
                <button type="submit">Submit</button>
              </form>
            </div>
          </div>

          <table id="employeeTable">
            <thead>
              <tr>
                {data.length > 0 &&
                  Object.keys(data[0]).map((key) => (
                    key !== '_id' && (
                      <th key={key}>{key}</th>
                    )
                  ))}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage).map((item) => (
                <tr key={item._id}>
                  {Object.keys(item).map((key) => (
                    key !== '_id' && (
                      <td key={`${item._id}-${key}`}>{item[key]}</td>
                    )
                  ))}
                  <td>
                    <button className="edit-link" onClick={() => openEditForm(item)}>Edit</button>
                    <button className="delete-link" onClick={() => handleDelete(item._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination">
            <button id="prevPageBtn" onClick={prevPage} disabled={currentPage === 1}>Previous Page</button>
            <button id="nextPageBtn" onClick={nextPage} disabled={currentPage * itemsPerPage >= data.length}>Next Page</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Assets;
