import React, { useState, useEffect } from 'react';
import '../css/Assets.css';
import TopNav from '../pages/TopNav'; // Assuming TopNav.jsx is correctly imported
import EditForm from '../pages/EditForm'; // Import EditForm component
import axios from 'axios';

function Assets() {
  const [data, setData] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentItem, setCurrentItem] = useState(null);

  useEffect(() => {
    fetchData(); // Fetch data on component mount
  }, []); // Empty dependency array ensures this effect runs only once

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/admin/genReport');
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/admin/assets/${id}`);
      fetchData(); // Refresh the data
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  const handleEdit = (item) => {
    setCurrentItem(item);
    setIsEditing(true);
  };

  const handleSave = async (updatedItem) => {
    try {
      await axios.put(`http://localhost:5000/admin/assets/${updatedItem._id}`, updatedItem);
      setIsEditing(false);
      setCurrentItem(null);
      fetchData(); // Refresh the data
    } catch (error) {
      console.error('Error updating data:', error);
    }
  };

  return (
    <div className="report-page">
      <TopNav /> {/* Include TopNav component */}
      <div className="content gen-report-content">
        <div className="table-container">
          <table id="employeeTable">
            <thead>
              <tr>
                {data.length > 0 && Object.keys(data[0]).map((key, index) => (key !== '_id' && (
                  <th key={key} className={index > 8 ? 'extra-column' : ''}>{key}</th>
                )))}
                <th>Actions</th> {/* Add a header for actions */}
              </tr>
            </thead>
            <tbody>
              {data.map((item) => (
                <tr key={item._id}>
                  {Object.keys(item).map((key, colIndex) => (key !== '_id' && (
                    <td key={`${item._id}-${key}`} className={colIndex > 8 ? 'extra-column' : ''}>{item[key]}</td>
                  )))}
                  <td>
                    <button onClick={() => handleEdit(item)}>Edit</button>
                    <button onClick={() => handleDelete(item._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {isEditing && currentItem && (
          <EditForm
            currentItem={currentItem}
            setIsEditing={setIsEditing}
            handleSave={handleSave}
          />
        )}
      </div>
    </div>
  );
}

export default Assets;
