import React, { useState, useEffect } from 'react';
import '../css/Assets.css';
import TopNav from '../pages/TopNav'; // Assuming TopNav.jsx is correctly imported

function Assets() {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [showFullTable, setShowFullTable] = useState(false);
  const [searchInput, setSearchInput] = useState('');
  const [editingItemId, setEditingItemId] = useState(null);
  const [editFormData, setEditFormData] = useState({});

  useEffect(() => {
    fetchData(); // Fetch data on component mount
  }, []); // Empty dependency array ensures this effect runs only once

  useEffect(() => {
    showCurrentPage(); // Update table display on pagination or item per page change
  }, [currentPage, itemsPerPage]); // Run whenever currentPage or itemsPerPage changes

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:5000/admin/genReport');
      const data = await response.json();
      setData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const changeItemsPerPage = (event) => {
    setItemsPerPage(parseInt(event.target.value, 10));
    setCurrentPage(1); // Reset to first page when items per page changes
  };

  const nextPage = () => {
    if ((currentPage + 1) * itemsPerPage <= data.length) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const showCurrentPage = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const tableRows = document.getElementById("employeeTable").querySelectorAll("tbody tr");

    tableRows.forEach((row, index) => {
      if (index >= startIndex && index < endIndex) {
        row.style.display = "";
      } else {
        row.style.display = "none";
      }
    });

    // Update pagination buttons
    document.getElementById("prevPageBtn").disabled = (currentPage === 1);
    document.getElementById("nextPageBtn").disabled = ((currentPage + 1) * itemsPerPage > tableRows.length);
  };

  const openForm = () => {
    document.getElementById("addDataForm").style.display = "block";
  };

  const closeForm = () => {
    document.getElementById("addDataForm").style.display = "none";
  };

  const openFieldForm = () => {
    document.getElementById("addFieldForm").style.display = "block";
  };

  const closeFieldForm = () => {
    document.getElementById("addFieldForm").style.display = "none";
  };

  const toggleColumns = () => {
    setShowFullTable(!showFullTable);
    const extraColumns = document.querySelectorAll(".extra-column");

    extraColumns.forEach(column => {
      column.style.display = showFullTable ? "table-cell" : "none";
    });
  };

  const searchTable = () => {
    const filter = searchInput.toUpperCase();
    const table = document.getElementById("employeeTable");
    const tr = table.getElementsByTagName("tr");

    for (let i = 1; i < tr.length; i++) { // Start from 1 to skip table headers
      let display = "none";
      const td = tr[i].getElementsByTagName("td");
      for (let j = 0; j < td.length; j++) {
        const txtValue = td[j].textContent || td[j].innerText;
        if (txtValue.toUpperCase().indexOf(filter) > -1) {
          display = "";
          break;
        }
      }
      tr[i].style.display = display;
    }
  };

  const handleEdit = (item) => {
    setEditingItemId(item._id);
    setEditFormData(item); // Populate edit form data with the current item's data
  };

  const handleEditInputChange = (e) => {
    setEditFormData({
      ...editFormData,
      [e.target.name]: e.target.value
    });
  };

  const handleEditSubmit = async () => {
    try {
      const response = await fetch(`/edit_asset/${editingItemId}`, {
        method: 'PUT', // Assuming PUT method for editing
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editFormData),
      });

      if (response.ok) {
        // Update data locally after successful edit
        const updatedData = data.map(item => item._id === editingItemId ? editFormData : item);
        setData(updatedData);
        setEditingItemId(null); // Clear editing state
        setEditFormData({}); // Clear edit form data
      } else {
        console.error('Failed to update item');
      }
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  return (
    <div className="report-page">
      <TopNav /> {/* Include TopNav component */}
      <div className="content gen-report-content">
        <div className="table-container">
          <div className="items-per-page">
            <label htmlFor="itemsPerPage">Items per page:</label>
            <select id="itemsPerPage" onChange={changeItemsPerPage} value={itemsPerPage}>
              <option value="10">10</option>
              <option value="20">20</option>
              <option value="50">50</option>
              <option value="100">100</option>
            </select>
          </div>

          <input
            type="text"
            id="searchInput"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            onKeyUp={searchTable}
            placeholder="Search for names.."
          />

          <button id="addDataBtn" onClick={openForm}>Add Data</button>
          <button id="addFieldBtn" onClick={openFieldForm}>Add Field</button>
          <button id="toggleColumnsBtn" onClick={toggleColumns}>
            {showFullTable ? "Show Limited Table" : "Show Full Table"}
          </button>

          <div id="addDataForm" className="modal">
            <div className="modal-content">
              <span className="close" onClick={closeForm}>&times;</span>
              <h2>Add New Data</h2>
              <form id="dataForm">
                {data.length > 0 &&
                  Object.keys(data[0]).map((key) => (
                    key !== '_id' && (
                      <div key={key}>
                        <label htmlFor={key}>{key}:</label>
                        <input type="text" id={key} name={key} required /><br />
                      </div>
                    )
                  ))}
                <button type="submit">Submit</button>
              </form>
            </div>
          </div>

          <div id="addFieldForm" className="modal">
            <div className="modal-content">
              <span className="close" onClick={closeFieldForm}>&times;</span>
              <h2>Add New Field</h2>
              <form id="fieldForm">
                <label htmlFor="fieldName">Field name:</label>
                <input type="text" id="fieldName" name="fieldName" required /><br />
                <label htmlFor="fieldType">Field type:</label>
                <select id="fieldType" name="fieldType" required>
                  <option value="String">String</option>
                  <option value="Number">Number</option>
                  <option value="Boolean">Boolean</option>
                </select><br />
                <button type="submit">Submit</button>
              </form>
            </div>
          </div>

          <table id="employeeTable">
            <thead>
              <tr>
                {data.length > 0 &&
                  Object.keys(data[0]).map((key, index) => (
                    key !== '_id' && (
                      <th key={key} className={index > 8 ? 'extra-column' : ''}>{key}</th>
                    )
                  ))}
                <th className="extra-column">Actions</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, rowIndex) => (
                <tr key={item._id}>
                  {Object.keys(item).map((key, colIndex) => (
                    key !== '_id' && (
                      <td key={`${item._id}-${key}`} className={colIndex > 8 ? 'extra-column' : ''}>{editingItemId === item._id ?
                        <input
                          type="text"
                          name={key}
                          value={editFormData[key] || item[key]}
                          onChange={handleEditInputChange}
                        />
                        :
                        item[key]
                      }</td>
                    )
                  ))}
                  <td className="extra-column">
                    {editingItemId === item._id ?
                      <button onClick={handleEditSubmit}>Save</button>
                      :
                      <>
                        <button onClick={() => handleEdit(item)}>Edit</button>
                        <a href={`/delete_asset/${item._id}`} className="delete-link" onClick={() => confirm('Are you sure you want to delete this item?')}>Delete</a>
                      </>
                    }
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="pagination">
            <button id="prevPageBtn" onClick={prevPage}>Previous Page</button>
            <button id="nextPageBtn" onClick={nextPage}>Next Page</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Assets;
